mapper.select:
SELECT id,status FROM user
WHERE account=#{account}
AND password=#{password}

mapper.update:
UPDATE user
name=#{name}, image=#{image}, intro=#{intro},
WHERE id = #{id}

UPDATE user
SET latitude=#{latitude},
longitude=#{longitude}
WHERE id=#{id}

UPDATE user
SET passowrd=#{password}
WHERE id=#{id}

UPDATE user
SET integral=integral+#{integral}
WHERE id=#{id}

UPDATE user
SET status=#{status}
WHERE id=#{id}

mapper.select:
SELECT * FROM user
WHERE ( longitude-#{scope} <#{longitude} OR longitude+#{scope} >#{longitude} ) AND
(latitude-#{scope} < #{latitude} OR latitude+#{scope} > #{latitude});

SELECT COUNT(book_id)
FROM reserve
WHERE user_id=#{id}

SELECT COUNT(book_id)
FROM collect
WHERE user_id=#{id}

SELECT longitude,latitude
FROM user
WHERE id=#{id}

SELECT id,name
FROM user
WHERE account LIKE ${condition}
OR name LIKE ${condition}

mapper.update:
UPDATE borrow
SET comment=#{comment},
giveMark=#{giveMark}
WHERE book_id=#{book_id}
AND user_id=#{borrow_id}

UPDATE reserve
SET give_me=TRUE
WHERE user_id=#{reserve_id}
AND book_id=#{book_id}

UPDATE borrow
SET status=#{status}
WHERE book_id=#{book_id}
AND user_id=#{borrow_id}

mapper.delete:
DELETE FROM reserve
WHERE book_id=#{book_id}
AND user_id=#{user_id}

mapper.select:
SELECT give_me
FROM reserve
WHERE book_id=#{book_id}
AND user_id=#{reserve_id}
